import { Component, OnInit } from '@angular/core';
import { StaffService } from 'src/app/services/staff.service';
import * as pdfMake from 'pdfmake/build/pdfmake.js';
import * as pdfFonts from 'pdfmake/build/vfs_fonts.js';
import * as moment from 'moment';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-staff-salary',
  templateUrl: './staff-salary.component.html',
  styleUrls: ['./staff-salary.component.css', '../../admin/admin.css'],
})
export class StaffSalaryComponent implements OnInit {
  dpart: string = 'Salary';
  loggedInData: any;
  allSalaryData: any;
  allSalaryDataLength: number;

  constructor(private staffService: StaffService) {}

  ngOnInit(): void {
    this.loggedInData = this.staffService.loggednInData();

    this.staffService.getSalaryForStaff(this.loggedInData.empid).subscribe(
      (res) => {
        // console.log(res);
        if (res.length != 0) {
          this.allSalaryData = res;
          this.allSalaryDataLength = res.length;
        } else {
          this.allSalaryDataLength = 0;
          this.allSalaryData = [];
        }
      },
      (err) => {
        console.log(err);
      }
    );
  }

  pdfGenerate(data: any, action) {
    // console.log(data);

    let pdfDefinition = {
      content: [
        {
          image:
            'data:image/jpeg;base64,',
          alignment: 'center',
          width: 150,
        },

        {
          text: 'Salary Slip',
          fontSize: 14,
          bold: true,
          decoration: 'underline',
          color: '#4ca745',
          alignment: 'center',
        },
        {
          text: 'Staff Details',
          style: 'sectionHeader',
        },
        {
          columns: [
            [
              {
                text: 'Name : ' + this.loggedInData.fname,
                bold: true,
              },
              {
                text: 'Address : ' + this.loggedInData.address,
                margin: [0, 5, 0, 0],
              },
              {
                text: 'Contact : ' + this.loggedInData.mobile,
                margin: [0, 5, 0, 0],
              },
            ],
            [
              {
                text: 'Date : ' + moment(data.salarydate).format('DD-MM-YYYY'),
                alignment: 'right',
                margin: [0, 5, 0, 0],
              },
              {
                text: 'Email : ' + this.loggedInData.email,
                alignment: 'right',
                margin: [0, 5, 0, 0],
              },
            ],
          ],
        },
        {
          text: 'Salary Details',
          style: 'sectionHeader',
        },
        { text: 'Earnings(+)', style: 'titleHeader', color: '#4ca745' },
        {
          table: {
            widths: ['*', '*', '*', '*'],
            body: [
              [
                'Basic Salary',
                'HRA(+22%)',
                'Medical Allowances(+8%)',
                'Dearness Allowances(+6.5%)',
              ],
              [
                data.basicsalary,
                data.hra,
                data.medicalallow,
                data.dearnessallow,
              ],
            ],
          },
        },
        {
          text: 'Deductions(-)',
          style: 'titleHeader',
          color: '#d9534f',
        },
        {
          table: {
            widths: ['*', '*', '*'],
            body: [
              ['EPF(-9%)', 'Health Insurance(-3%)', 'Tax(-5%)'],
              [data.epf, data.healthinsurance, data.tax],
            ],
          },
        },
        {
          columns: [
            [
              {
                text: 'Net Payment: ',
                bold: true,
              },
            ],
            [
              {
                text: '₹' + data.netpay,
                alignment: 'right',
                bold: true,
                color: '#4ca745',
              },
            ],
          ],
          margin: [0, 8, 0, 0], // [right,top,left,bottom]
        },
        {
          text: 'Signature And Office Stamp!!',
          alignment: 'right',
          // color: '#191eff',
          margin: [0, 25, 0, 0],
        },
        {
          image:
            'data:image/png;base64,',
          alignment: 'right',
          width: 70,
          margin: [35, 5, 0, 0],
        },
      ],
      styles: {
        sectionHeader: {
          bold: true,
          decoration: 'underline',
          fontSize: 14,
          margin: [0, 15, 0, 15], // [right,top,left,bottom]
        },
        titleHeader: {
          bold: true,
          fontSize: 10,
          margin: [0, 10, 0, 5], // [right,top,left,bottom]
        },
      },
    };

    if (action === 'download') {
      pdfMake
        .createPdf(pdfDefinition)
        .download(
          `Salary-${moment(data.salarydate).format('DD-MM-YYYY')}-${
            data.fname
          }.pdf`
        );
    } else if (action === 'print') {
      pdfMake.createPdf(pdfDefinition).print();
    } else {
      pdfMake.createPdf(pdfDefinition).open();
    }
  }
}
